// Simulação básica de jQuery
window.$ = window.jQuery = function (selector) {
    return {
        on: function (event, callback) {
            console.log(`Simulando evento: ${event} em ${selector}`);
            callback();
        },
        val: function (value) {
            console.log(`Definindo valor de ${selector} para: ${value}`);
            return this;
        }
    };
};
